const ComprehensiveServerSide = () => {
  const [selected, setSelected] = useState<Set<string | number> | 'all'>(
    new Set(),
  );

  const typeOptions = [
    { value: '', label: 'All types' },
    { value: 'service', label: 'Service' },
    { value: 'website', label: 'Website' },
    { value: 'library', label: 'Library' },
    { value: 'documentation', label: 'Documentation' },
    { value: 'other', label: 'Other' },
  ];

  const columns: ColumnConfig<Data1Item>[] = [
    {
      id: 'name',
      label: 'Name',
      isRowHeader: true,
      isSortable: true,
      cell: item => (
        <CellText title={item.name} description={item.description} />
      ),
    },
    {
      id: 'owner',
      label: 'Owner',
      isSortable: true,
      cell: item => <CellText title={item.owner.name} />,
    },
    {
      id: 'type',
      label: 'Type',
      isSortable: true,
      cell: item => <CellText title={item.type} />,
    },
    {
      id: 'lifecycle',
      label: 'Lifecycle',
      isSortable: true,
      cell: item => <CellText title={item.lifecycle} />,
    },
  ];

  const { tableProps, search, filter } = useTable<Data1Item, TypeFilter>({
    mode: 'offset',
    initialSort: { column: 'name', direction: 'ascending' },
    getData: async ({
      offset,
      pageSize,
      sort,
      filter: typeFilter,
      search: searchQuery,
    }) => {
      // Simulate server-side filtering, sorting, and pagination
      // with slower and slower responses
      const page = Math.floor(offset / pageSize) + 1;
      await new Promise(resolve => setTimeout(resolve, 300 * page));

      let filtered = [...data1];

      // Apply search filter
      if (searchQuery) {
        const query = searchQuery.toLowerCase();
        filtered = filtered.filter(
          item =>
            item.name.toLowerCase().includes(query) ||
            item.owner.name.toLowerCase().includes(query) ||
            item.description?.toLowerCase().includes(query),
        );
      }

      // Apply type filter
      if (typeFilter?.type) {
        filtered = filtered.filter(item => item.type === typeFilter.type);
      }

      // Apply sorting
      if (sort) {
        filtered.sort((a, b) => {
          let aVal: string;
          let bVal: string;
          switch (sort.column) {
            case 'owner':
              aVal = a.owner.name;
              bVal = b.owner.name;
              break;
            case 'type':
              aVal = a.type;
              bVal = b.type;
              break;
            case 'lifecycle':
              aVal = a.lifecycle;
              bVal = b.lifecycle;
              break;
            default:
              aVal = a.name;
              bVal = b.name;
          }
          const cmp = aVal.localeCompare(bVal);
          return sort.direction === 'descending' ? -cmp : cmp;
        });
      }

      return {
        data: filtered.slice(offset, offset + pageSize),
        totalCount: filtered.length,
      };
    },
    paginationOptions: { pageSize: 10 },
  });

  return (
    <Flex direction="column" gap="4">
      <Flex gap="4" align="end">
        <SearchField
          aria-label="Search"
          label="Search"
          placeholder="Search by name, owner, or description..."
          style={{ width: 300 }}
          {...search}
        />
        <Select
          label="Type"
          options={typeOptions}
          value={filter.value?.type ?? ''}
          onChange={key =>
            filter.onChange({ type: key === '' ? null : String(key) })
          }
          style={{ width: 180 }}
        />
      </Flex>
      <Table
        {...tableProps}
        columnConfig={columns}
        selection={{
          mode: 'multiple',
          behavior: 'toggle',
          selected,
          onSelectionChange: setSelected,
        }}
        emptyState={
          search.value || filter.value?.type ? (
            <div>No results match your filters</div>
          ) : (
            <div>No data available</div>
          )
        }
      />
    </Flex>
  );
};

