    isOpen={isOpen}
    isDisabled={isDisabled}
    delay={delay}
    closeDelay={closeDelay}
  >
    <Button>Button</Button>
    <Tooltip>{children ?? 'I am a tooltip'}</Tooltip>
  </TooltipTrigger>
);

